Frontend/src/components/HeroSection.css.content:
.hero-section {
  min-height: 100vh;
  background: #1a1a1a;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
}

/* Live moving pattern background */
.hero-section::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 200%;
  height: 200%;
  background: 
    linear-gradient(45deg, transparent 40%, rgba(74, 130, 153, 0.03) 50%, transparent 60%),
    linear-gradient(-45deg, transparent 40%, rgba(74, 130, 153, 0.02) 50%, transparent 60%);
  background-size: 60px 60px;
  animation: movePattern 20s linear infinite;
  z-index: 1;
}

@keyframes movePattern {
  0% {
    transform: translate(-50%, -50%);
  }
  100% {
    transform: translate(-50%, -50%) translate(60px, 60px);
  }
}

/* Geometric shapes animation */
.hero-section::after {
  content: '';
  position: absolute;
  top: 20%;
  right: 10%;
  width: 100px;
  height: 100px;
  border: 1px solid rgba(74, 130, 153, 0.1);
  border-radius: 50%;
  animation: float2 8s ease-in-out infinite;
  z-index: 1;
}

@keyframes float2 {
  0%, 100% {
    transform: translateY(0px) rotate(0deg);
    opacity: 0.3;
  }
  50% {
    transform: translateY(-20px) rotate(180deg);
    opacity: 0.6;
  }
}

.hero-content {
  display: flex;
  align-items: center;
  justify-content: center;
  max-width: 1400px;
  width: 100%;
  min-height: 100vh;
  margin: 0 auto;
  position: relative;
  z-index: 2;
}


/* Accessibility: Respect reduced motion preferences */
@media (prefers-reduced-motion: reduce) {
  .hero-section::before,
  .hero-section::after {
    animation: none;
  }
}

.hero-text {
  padding: 4rem 2rem;
  background: transparent;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  width: 100%;
  position: relative;
  z-index: 2;
}

.hero-title {
  font-size: 3rem;
  font-weight: 400;
  line-height: 1.2;
  margin-bottom: 2rem;
  color: #ffffff;
  letter-spacing: -0.02em;
  font-family: 'Inter', system-ui, -apple-system, sans-serif;
  text-align: center;
  max-width: 1400px;
}

.gradient-text {
  color: #000000;
  font-weight: 300;
}

.highlight-text {
  color: #4a8299;
  font-weight: 400;
}

.hero-subtitle {
  font-size: 1.1rem;
  color: #cccccc;
  line-height: 1.6;
  margin-bottom: 3rem;
  font-weight: 300;
  max-width: 600px;
  letter-spacing: 0.01em;
  text-align: center;
}

.hero-input-section {
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

.input-container {
  position: relative;
  width: 100%;
  max-width: 400px;
}

.hero-input {
  width: 100%;
  padding: 1rem 1rem 1rem 1rem;
  border: none;
  border-bottom: 1px solid #cccccc;
  background: transparent;
  font-size: 1rem;
  font-family: inherit;
  color: #000000;
  transition: all 0.3s ease;
}

.hero-input:focus {
  outline: none;
  border-bottom-color: #000000;
}

.hero-input::placeholder {
  color: #999999;
  font-weight: 300;
}

.input-submit-btn {
  position: absolute;
  right: 0;
  bottom: 0;
  background: #000000;
  color: white;
  border: none;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.2s ease;
  font-size: 1rem;
}

.input-submit-btn:hover {
  background: #333333;
}

.action-tags {
  display: none;
}

.hero-3d {
  background: #000000;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
}

.hero-visual {
  width: 100%;
  height: 100vh;
  background: #000000;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
}

.floating-pdf {
  width: 300px;
  height: 400px;
  background: #ffffff;
  border-radius: 8px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  transform: rotate(-5deg);
  animation: float 6s ease-in-out infinite;
}

.pdf-icon {
  font-size: 4rem;
