  margin-bottom: 1rem;
}

.pdf-text {
  color: #000000;
  font-weight: 300;
  text-align: center;
  font-size: 0.9rem;
}

@keyframes float {
  0%, 100% {
    transform: rotate(-5deg) translateY(0px);
  }
  50% {
    transform: rotate(-5deg) translateY(-10px);
  }
}

/* Responsive design */
@media (max-width: 1024px) {
  .hero-content {
    grid-template-columns: 1fr;
    min-height: auto;
  }

  .hero-text {
    padding: 3rem 2rem;
    text-align: center;
