import React, { useState, useMemo, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { motion } from 'framer-motion'
import { FileText, Download, Plus, Settings, LogOut, User } from 'lucide-react'
import { useAuth } from '../contexts/AuthContext'
import { dashboardApi } from '../lib/dashboardApi'
import type { DashboardStats, LeadMagnet } from '../lib/dashboardApi'
import LeadMagnetFormContent from './LeadMagnetFormContent'
import './Dashboard.css'

interface DashboardProps {}

const Dashboard: React.FC<DashboardProps> = () => {
  const { user, logout } = useAuth()
  const navigate = useNavigate()
  const [searchQuery, setSearchQuery] = useState('')
  const [stats, setStats] = useState<DashboardStats | null>(null)
  const [projects, setProjects] = useState<LeadMagnet[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [showLeadMagnetForm, setShowLeadMagnetForm] = useState(false)

  const handleLogout = () => {
    logout()
    navigate('/')
  }

  // Fetch dashboard data on component mount
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true)
        const [statsData, projectsData] = await Promise.all([
          dashboardApi.getStats(),
          dashboardApi.getLeadMagnets()
        ])
        setStats(statsData)
        setProjects(projectsData)
        setError(null)
      } catch (err) {
        console.error('Failed to fetch dashboard data:', err)
        setError('Failed to load dashboard data')
      } finally {
        setLoading(false)
      }
    }

    fetchDashboardData()
  }, [])

  const filteredProjects = useMemo(() => {
    if (!searchQuery.trim()) return projects
    const query = searchQuery.toLowerCase()
    return projects.filter(project => 
      project.title.toLowerCase().includes(query) ||
      project.status.toLowerCase().includes(query)
    )
  }, [projects, searchQuery])

  const formatDate = (dateString: string) => {
    const date = new Date(dateString)
    const now = new Date()
    const diffInDays = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60 * 24))

    if (diffInDays === 0) return 'Today'
    if (diffInDays === 1) return '1 day ago'
    if (diffInDays < 7) return `${diffInDays} days ago`
    return date.toLocaleDateString()
  }

  if (loading) {
    return (
      <div className="dashboard">
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', color: '#ffffff' }}>
          Loading dashboard...
        </div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="dashboard">
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', color: '#ff6b6b' }}>
          {error}
        </div>
      </div>
    )
  }

  return (
    <div className="dashboard">
      <nav className="dashboard-nav">
        <div className="nav-brand">Forma</div>
        <div className="nav-actions">
          <div className="user-menu">
            <User className="user-icon" size={20} />
            <span className="user-name">{user?.name}</span>
            <button className="logout-btn" onClick={handleLogout}>
              <LogOut size={18} />
            </button>
          </div>
        </div>
      </nav>

      <div className="dashboard-layout">
        <aside className="dashboard-sidebar">
          <div className="sidebar-brand">
            <div className="brand-icon">ðŸ“„</div>
            <div className="brand-info">
              <h3>AI Lead Magnets</h3>
              <p>Your AI Workforce</p>
            </div>
          </div>

          <button 
            className="sidebar-create-btn"
            onClick={() => setShowLeadMagnetForm(true)}
          >
            <Plus size={20} />
            Create Lead Magnet
          </button>

          <div className="sidebar-section">
            <h4>Navigation</h4>
            <nav className="sidebar-nav">
              <a href="#" className="nav-item active">
                <FileText size={18} />
                My Lead Magnets
              </a>
              <a href="#" className="nav-item">
                <Download size={18} />
                Active Campaigns
              </a>
              <a href="#" className="nav-item">
                <Settings size={18} />
                Integrations
              </a>
              <a href="#" className="nav-item">
                <User size={18} />
                Analytics
              </a>
            </nav>
          </div>

          <div className="sidebar-section">
            <h4>Account</h4>
            <nav className="sidebar-nav">
              <a href="#" className="nav-item">
                <Settings size={18} />
                Settings
              </a>
            </nav>
          </div>
        </aside>

        <main className="dashboard-main-content">
          {showLeadMagnetForm ? (
            <LeadMagnetFormContent 
              onClose={() => setShowLeadMagnetForm(false)}
              onSuccess={(message) => {
                setShowLeadMagnetForm(false);
                // Refresh dashboard data
                const fetchDashboardData = async () => {
                  try {
                    const [statsData, projectsData] = await Promise.all([
                      dashboardApi.getStats(),
                      dashboardApi.getLeadMagnets()
                    ])
                    setStats(statsData)
                    setProjects(projectsData)
                  } catch (err) {
                    console.error('Failed to refresh dashboard data:', err)
                  }
                }
                fetchDashboardData();
              }}
            />
          ) : (
            <>
              <div className="main-header">
                <div className="header-top">
                  <motion.h1
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="main-title"
                  >
                    My Lead Magnets
                  </motion.h1>
                  <motion.button
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.1 }}
                    className="create-btn-header"
                    onClick={() => setShowLeadMagnetForm(true)}
                  >
                    <Plus size={20} />
                    Create Lead Magnet
                  </motion.button>
                </div>
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2 }}
              className="main-subtitle"
            >
              Manage and interact with your AI-powered lead magnets
            </motion.p>
          </div>

          <div className="stats-grid">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2 }}
              className="stat-card"
            >
              <div className="stat-header">
                <span className="stat-label">Total Lead Magnets</span>
                <FileText className="stat-icon" />
              </div>
              <div className="stat-value">{stats?.total_lead_magnets || 0}</div>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.3 }}
              className="stat-card"
            >
              <div className="stat-header">
                <span className="stat-label">Active Lead Magnets</span>
                <Download className="stat-icon" />
              </div>
              <div className="stat-value">{stats?.active_lead_magnets || 0}</div>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.4 }}
              className="stat-card"
            >
              <div className="stat-header">
                <span className="stat-label">Total Downloads</span>
                <Settings className="stat-icon" />
              </div>
              <div className="stat-value">{stats?.total_downloads || 0}</div>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.5 }}
              className="stat-card"
            >
              <div className="stat-header">
                <span className="stat-label">Leads Generated</span>
                <User className="stat-icon" />
              </div>
              <div className="stat-value">{stats?.leads_generated || 0}</div>
            </motion.div>
          </div>

          <div className="content-section">
            <div className="search-section">
              <div className="search-wrapper">
                <input
                  type="text"
                  placeholder="Search lead magnets..."
                  className="search-input"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>
            </div>

            <div className="projects-grid">
              {filteredProjects.length === 0 ? (
                <div className="no-results">
                  {searchQuery.trim() ? (
                    <p>No lead magnets found matching "{searchQuery}"</p>
                  ) : (
                    <div>
                      <p>No lead magnets created yet</p>
                      <p style={{ fontSize: '0.9rem', color: '#888', marginTop: '0.5rem' }}>Click "Create Lead Magnet" to get started</p>
                    </div>
                  )}
                </div>
              ) : (
                filteredProjects.map((project, index) => (
                  <motion.div
                    key={project.id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.5 + index * 0.1 }}
                    className={`project-card ${project.status}`}
                  >
                    <div className="project-header">
                      <div className="project-avatar">
                        <FileText className="project-icon" />
                      </div>
                      <div className="project-info">
                        <h3 className="project-title">{project.title}</h3>
                        <span className={`status-badge ${project.status}`}>
                          {project.status === 'in-progress' ? 'In Progress' : 
                           project.status === 'completed' ? 'Completed' : 'Draft'}
                        </span>
                      </div>
                      <button className="project-menu">â‹®</button>
                    </div>

                    <p className="project-description">
                      {project.description || 
                       (project.status === 'completed' ? 'Generates high-quality leads for architectural services' :
                        project.status === 'in-progress' ? 'Converting prospects into qualified leads' : 
                        'Draft lead magnet ready for review')}
                    </p>

                    <div className="project-metrics">
                      <div className="metric">
                        <span className="metric-value">{project.downloads_count}</span>
                        <span className="metric-label">Downloads</span>
                      </div>
                      <div className="metric">
                        <span className="metric-value">{project.leads_count}</span>
                        <span className="metric-label">Leads Generated</span>
                      </div>
                    </div>

                    <div className="project-footer">
                      <span className="project-last-activity">Created: {formatDate(project.created_at)}</span>
                    </div>
                  </motion.div>
                ))
              )}
            </div>
              </div>
            </>
          )}
        </main>
      </div>
    </div>
  )
}

export default Dashboard

Frontend/src/components/LoginPage.tsx.content:
import React, { useState } from 'react'
import { Link, useNavigate, useLocation } from 'react-router-dom'
import { motion } from 'framer-motion'
import { Eye, EyeOff, Mail, Lock, ArrowLeft } from 'lucide-react'
import { useAuth } from '../contexts/AuthContext'
import './AuthPages.css'

const LoginPage: React.FC = () => {
  const [formData, setFormData] = useState({
    username_or_email: '',
    password: '',
  })
  const [showPassword, setShowPassword] = useState(false)
  const [error, setError] = useState('')
  const [loading, setLoading] = useState(false)

  const { login } = useAuth()
  const navigate = useNavigate()
  const location = useLocation()

  // Get success message from navigation state
  const successMessage = location.state?.message

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    })
    if (error) setError('')
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    console.log('Login form submitted with data:', { username_or_email: formData.username_or_email, password: '***' })

    if (!formData.username_or_email || !formData.password) {
      setError('Please fill in all fields')
      return
    }

    setLoading(true)
    setError('')

    try {
      console.log('Calling login function...')
      await login(formData.username_or_email, formData.password)
      console.log('Login successful, navigating to dashboard...')
      navigate('/dashboard')
    } catch (err: any) {
      console.error('Login error:', err)
      if (err.response?.data) {
        const errorData = err.response.data
        if (errorData.non_field_errors && Array.isArray(errorData.non_field_errors)) {
          setError(errorData.non_field_errors[0])
        } else if (errorData.detail) {
          setError(errorData.detail)
        } else {
          setError('Login failed. Please check your credentials.')
        }
      } else if (err.code === 'ECONNABORTED' || err.message.includes('timeout')) {
        setError('Request timed out. Please try again.')
      } else if (err.request) {
        setError('Unable to connect to server. Please check your connection and try again.')
      } else {
        setError('An unexpected error occurred. Please try again.')
      }
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="auth-overlay">
      <motion.div 
        className="auth-container"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3 }}
        onClick={(e) => e.stopPropagation()}
      >
        <div className="auth-header">
          <Link to="/" className="back-button">
            <ArrowLeft size={20} />
            Back to Home
          </Link>
          <div className="auth-logo">Forma</div>
          <h2>Welcome Back</h2>
          <p>Sign in to your account</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          {successMessage && (
            <motion.div 
              className="auth-success"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              {successMessage}
            </motion.div>
          )}

          {error && (
            <motion.div 
              className="auth-error"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              {error}
            </motion.div>
          )}

          <div className="auth-field">
            <div className="input-wrapper">
              <Mail className="input-icon" size={20} />
              <input
                type="text"
                name="username_or_email"
                placeholder="Username or email address"
                value={formData.username_or_email}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="auth-field">
            <div className="input-wrapper">
              <Lock className="input-icon" size={20} />
              <input
                type={showPassword ? 'text' : 'password'}
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>
          </div>

          <button
            type="submit"
            className="auth-submit"
            disabled={loading}
          >
            {loading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            Don't have an account?{' '}
            <Link to="/signup" className="auth-link">
              Sign up here
            </Link>
          </p>
        </div>
      </motion.div>
    </div>
  )
}

export default LoginPage

