/* Lead Magnet Form Content - Dashboard Integration */
.lead-magnet-form-content {
  height: 100%;
  display: flex;
  flex-direction: column;
  color: #e2e8f0;
  background: transparent;
}

.form-header {
  margin-bottom: 2rem;
}

.header-actions {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 2rem;
}

.header-actions h1 {
