  cursor: pointer;
  font-size: 0.875rem;
}

.back-button:hover {
  background: #334155;
  border-color: #475569;
}

/* Progress Bar - Thin Loading Bar with Dots */
.progress-steps {
  display: flex;
  align-items: center;
  padding: 2rem;
  background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
  border-radius: 1rem;
  border: 1px solid #14b8a6;
  margin-bottom: 2rem;
  box-shadow: 0 4px 20px rgba(20, 184, 166, 0.1);
}

.progress-bar {
  display: flex;
  align-items: center;
  width: 100%;
  position: relative;
}

.progress-line {
  flex: 1;
  height: 3px;
  background: #334155;
  border-radius: 2px;
  position: relative;
  margin: 0 1rem;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #14b8a6, #0d9488);
  border-radius: 2px;
  transition: width 0.6s ease;
  position: relative;
}

.progress-dot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: #334155;
  border: 2px solid #475569;
  transition: all 0.4s ease;
  position: relative;
  z-index: 2;
}

.progress-dot.active {
  background: linear-gradient(135deg, #14b8a6 0%, #0d9488 100%);
  border-color: #14b8a6;
