}

.step-name {
  font-weight: 600;
  font-size: 0.875rem;
  margin-bottom: 0.25rem;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.progress-step.active .step-name {
  color: #14b8a6;
  font-weight: 600;
}

.progress-step.completed .step-name {
