  flex: 1;
  overflow-y: auto;
  padding-right: 0.5rem;
  margin-bottom: 1.5rem;
}

.stage-form {
  background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
  border-radius: 1.5rem;
  padding: 3rem;
  border: 1px solid #14b8a6;
  box-shadow: 0 8px 32px rgba(20, 184, 166, 0.1);
