.form-field small {
  display: block;
  color: #94a3b8;
  font-size: 0.75rem;
  margin-top: 0.25rem;
}

.form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
}

/* Checkbox and Radio Grids */
.checkbox-grid,
.radio-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1rem;
}

.checkbox-card,
.radio-card {
  display: block;
  background: #1e293b;
  border: 1px solid #334155;
  border-radius: 0.5rem;
  padding: 1rem;
  cursor: pointer;
  transition: all 0.2s;
}

.checkbox-card:hover,
.radio-card:hover {
  border-color: #14b8a6;
  background: #1e293b;
  transform: translateY(-2px);
  box-shadow: 0 4px 15px rgba(20, 184, 166, 0.1);
}

.checkbox-card input:checked + .checkbox-content,
.radio-card input:checked + .radio-content {
  color: #14b8a6;
}

.checkbox-card input:checked,
.radio-card input:checked {
  accent-color: #14b8a6;
}

.checkbox-card input,
.radio-card input {
  margin-bottom: 0.5rem;
}

.checkbox-title,
.radio-title {
  display: block;
  font-weight: 500;
  margin-bottom: 0.25rem;
}

.checkbox-description,
.radio-description {
  display: block;
  font-size: 0.75rem;
  color: #94a3b8;
  line-height: 1.3;
}

.checkbox-label {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem;
  border-radius: 0.25rem;
  cursor: pointer;
  transition: background-color 0.2s;
}

.checkbox-label:hover {
  background: #1e293b;
}

.checkbox-text {
  font-size: 0.875rem;
}

/* Navigation */
.stage-navigation {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 1rem;
  padding-top: 1.5rem;
  border-top: 1px solid #334155;
}

.nav-button-group {
  display: flex;
  gap: 1rem;
}

.nav-button {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  border-radius: 0.5rem;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.2s;
  cursor: pointer;
  border: none;
  font-size: 0.875rem;
}

.nav-button.primary {
  background: linear-gradient(135deg, #14b8a6 0%, #0d9488 100%);
  color: white;
  box-shadow: 0 4px 15px rgba(20, 184, 166, 0.3);
}

.nav-button.primary:hover:not(:disabled) {
  background: linear-gradient(135deg, #0d9488 0%, #0f766e 100%);
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(20, 184, 166, 0.4);
}

.nav-button.secondary {
  background: #334155;
  color: #e2e8f0;
  border: 1px solid #475569;
}

.nav-button.secondary:hover:not(:disabled) {
  background: #475569;
}

.nav-button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.generate-button {
  background: linear-gradient(135deg, #059669 0%, #047857 100%) !important;
  box-shadow: 0 4px 15px rgba(5, 150, 105, 0.4) !important;
}

.generate-button:hover:not(:disabled) {
  background: linear-gradient(135deg, #047857 0%, #065f46 100%) !important;
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(5, 150, 105, 0.5) !important;
}

/* Error Message */
