import React from 'react';
import type { LeadMagnetGeneration } from '../../lib/leadMagnetApi';
import { LEAD_MAGNET_TYPE_CHOICES, MAIN_TOPIC_CHOICES } from '../../lib/leadMagnetApi';

interface LeadMagnetBasicsFormProps {
  data: Partial<LeadMagnetGeneration>;
  onChange: (data: Partial<LeadMagnetGeneration>) => void;
}

const LeadMagnetBasicsForm: React.FC<LeadMagnetBasicsFormProps> = ({ data, onChange }) => {
  const handleInputChange = (field: keyof LeadMagnetGeneration, value: any) => {
    onChange({ [field]: value });
  };

  return (
    <div className="lead-magnet-basics-form">
      <h2>Lead Magnet Basics</h2>
      <p className="form-description">
        Choose the type and topic for your lead magnet to attract your ideal clients.
      </p>

      <div className="form-grid">
        <div className="form-section">
          <h3>Lead Magnet Type</h3>
          <p className="field-description">What format works best for your content?</p>

          <div className="radio-grid">
            {LEAD_MAGNET_TYPE_CHOICES.map(choice => (
              <label key={choice.value} className="radio-card">
                <input
                  type="radio"
                  name="lead_magnet_type"
                  value={choice.value}
                  checked={data.lead_magnet_type === choice.value}
                  onChange={(e) => handleInputChange('lead_magnet_type', e.target.value)}
                />
                <div className="radio-content">
                  <span className="radio-title">{choice.label}</span>
                  <span className="radio-description">
                    {getTypeDescription(choice.value)}
                  </span>
                </div>
              </label>
            ))}
          </div>
        </div>

        <div className="form-section">
          <h3>Main Topic</h3>
          <p className="field-description">What's the primary focus of this lead magnet?</p>

          <div className="radio-grid">
            {MAIN_TOPIC_CHOICES.map(choice => (
              <label key={choice.value} className="radio-card">
                <input
                  type="radio"
                  name="main_topic"
                  value={choice.value}
                  checked={data.main_topic === choice.value}
                  onChange={(e) => handleInputChange('main_topic', e.target.value)}
                />
                <div className="radio-content">
                  <span className="radio-title">{choice.label}</span>
                  <span className="radio-description">
                    {getTopicDescription(choice.value)}
                  </span>
                </div>
              </label>
            ))}
          </div>

          {data.main_topic === 'custom' && (
            <div className="form-field">
              <label htmlFor="custom_topic">Custom Topic</label>
              <input
                type="text"
                id="custom_topic"
                value={data.custom_topic || ''}
                onChange={(e) => handleInputChange('custom_topic', e.target.value)}
                placeholder="Describe your custom topic..."
                required
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const getTypeDescription = (type: string): string => {
  const descriptions: Record<string, string> = {
    'guide': 'Comprehensive how-to resource',
    'case_study': 'Real project showcase with results',
    'checklist': 'Step-by-step actionable list',
    'roi_calculator': 'Interactive value demonstration tool',
    'trends_report': 'Industry insights and predictions',
    'client_onboarding_flow': 'Process overview for new clients',
    'design_portfolio': 'Curated project showcase',
    'custom': 'Something unique to your practice',
  };
  return descriptions[type] || '';
};

const getTopicDescription = (topic: string): string => {
  const descriptions: Record<string, string> = {
    'sustainable_architecture': 'Green building and eco-friendly design',
    'smart_homes': 'Technology-integrated residential design',
    'adaptive_reuse': 'Repurposing existing structures',
    'wellness_biophilic': 'Health-focused and nature-integrated design',
    'modular_prefab': 'Efficient, factory-built construction',
    'urban_placemaking': 'Community-focused public spaces',
    'passive_house_net_zero': 'Ultra-efficient energy design',
    'climate_resilient': 'Future-proof, weather-resistant design',
    'project_roi': 'Investment returns and value creation',
    'branding_differentiation': 'Standing out in the market',
    'custom': 'Your unique area of expertise',
  };
  return descriptions[topic] || '';
};

export default LeadMagnetBasicsForm;

Frontend/src/components/forms/QuestionCa.content:
/* Question Card Styles - Metallic Grey */
.question-card {
  background: linear-gradient(135deg, #c0c0c0 0%, #a8a8a8 100%);
  border-radius: 1rem;
  padding: 2.5rem;
  border: 1px solid #999999;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.2), 0 2px 4px -1px rgba(0, 0, 0, 0.1);
  position: relative;
  max-width: 700px;
  margin: 0 auto;
}

.question-header {
  text-align: left;
  margin-bottom: 2rem;
}

.question-title {
  color: #000000;
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
}

.question-description {
  color: #6b7280;
  font-size: 0.95rem;
  line-height: 1.5;
}

.question-content {
  margin-bottom: 2rem;
}

.question-actions {
  display: flex;
  justify-content: flex-end;
  gap: 1rem;
  padding-top: 1.5rem;
}

.question-btn {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  border-radius: 0.5rem;
  font-weight: 500;
  font-size: 0.875rem;
  text-decoration: none;
  transition: all 0.2s ease;
  cursor: pointer;
  border: none;
  min-width: 100px;
  justify-content: center;
}

.question-btn.primary {
  background: #14b8a6;
  color: white;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
}

.question-btn.primary:hover:not(:disabled) {
  background: #0d9488;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
}

.question-btn.secondary {
  background: #f9fafb;
  color: #374151;
  border: 1px solid #d1d5db;
}

.question-btn.secondary:hover {
  background: #f3f4f6;
  border-color: #9ca3af;
}

.question-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none;
}

/* Form Sections */
.question-card .form-section {
  margin-bottom: 2.5rem;
}

.question-card .form-section h4 {
  color: #000000;
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 1rem;
  border-bottom: 2px solid #999999;
  padding-bottom: 0.5rem;
}

/* Form Fields in Question Cards */
.question-card .form-field {
  margin-bottom: 1.5rem;
}

.question-card .form-field label {
  display: block;
  color: #000000;
  font-weight: 500;
  margin-bottom: 0.5rem;
  font-size: 0.875rem;
}

.question-card .form-field input,
.question-card .form-field select,
.question-card .form-field textarea {
  width: 100%;
  padding: 0.75rem;
  background: #ffffff;
  border: 1px solid #d1d5db;
  border-radius: 0.5rem;
  color: #111827;
  font-size: 0.875rem;
  transition: border-color 0.2s ease, box-shadow 0.2s ease;
}

.question-card .form-field input:focus,
.question-card .form-field select:focus,
.question-card .form-field textarea:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.question-card .form-field input::placeholder,
.question-card .form-field textarea::placeholder {
  color: #9ca3af;
}

.question-card .form-field small {
  display: block;
  color: #6b7280;
  font-size: 0.75rem;
  margin-top: 0.25rem;
}

/* Form Row in Question Cards */
.question-card .form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1.5rem;
}

/* Checkbox and Radio Grids in Question Cards */
.question-card .checkbox-grid,
.question-card .radio-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 1rem;
}

.question-card .checkbox-card,
.question-card .radio-card {
  display: block;
  background: rgba(15, 23, 42, 0.8);
  border: 2px solid rgba(20, 184, 166, 0.3);
  border-radius: 0.75rem;
  padding: 1.25rem;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
}

.question-card .checkbox-card:hover,
.question-card .radio-card:hover {
  border-color: #14b8a6;
  background: rgba(15, 23, 42, 1);
  transform: translateY(-2px);
  box-shadow: 0 4px 15px rgba(20, 184, 166, 0.2);
}

.question-card .checkbox-card input:checked + .checkbox-content,
.question-card .radio-card input:checked + .radio-content {
  color: #14b8a6;
}

.question-card .checkbox-card input:checked,
.question-card .radio-card input:checked {
  accent-color: #14b8a6;
}

.question-card .checkbox-card input,
.question-card .radio-card input {
  margin-bottom: 0.75rem;
}

.question-card .checkbox-title,
.question-card .radio-title {
  display: block;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #e2e8f0;
  font-size: 1rem;
}

.question-card .checkbox-description,
.question-card .radio-description {
  display: block;
  font-size: 0.875rem;
  color: #94a3b8;
  line-height: 1.4;
}

/* Checkbox Labels in Question Cards */
.question-card .checkbox-label {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.75rem;
  border-radius: 0.5rem;
  cursor: pointer;
  transition: all 0.2s ease;
  border: 1px solid #e5e7eb;
  margin-bottom: 0.5rem;
}

.question-card .checkbox-label:hover {
  background: #f9fafb;
  border-color: #d1d5db;
}

.question-card .checkbox-text {
  font-size: 0.875rem;
  color: #000000;
  font-weight: 400;
}

/* Responsive Design */
@media (max-width: 768px) {
  .question-card {
    padding: 2rem;
    margin: 0 1rem;
  }

  .question-title {
    font-size: 1.5rem;
  }

  .question-description {
    font-size: 1rem;
  }

  .question-actions {
    flex-direction: column;
  }

  .question-card .form-row {
    grid-template-columns: 1fr;
  }

  .question-card .checkbox-grid,
  .question-card .radio-grid {
    grid-template-columns: 1fr;
  }
}

import React from 'react';
import { motion } from 'framer-motion';

interface QuestionCardProps {
  title: string;
  description?: string;
  children: React.ReactNode;
  onNext?: () => void;
  onBack?: () => void;
  nextDisabled?: boolean;
  isLastCard?: boolean;
  showBack?: boolean;
  nextLabel?: string;
}

const QuestionCard: React.FC<QuestionCardProps> = ({
  title,
  description,
  children,
  onNext,
  onBack,
  nextDisabled = false,
  isLastCard = false,
  showBack = true,
  nextLabel
}) => {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.4 }}
      className="question-card"
    >
      <div className="question-header">
        <h3 className="question-title">{title}</h3>
        {description && (
          <p className="question-description">{description}</p>
        )}
      </div>

      <div className="question-content">
        {children}
      </div>

      <div className="question-actions">
        {showBack && onBack && (
          <button
            type="button"
            onClick={onBack}
            className="question-btn secondary"
          >
            Back
          </button>
        )}

        {onNext && (
          <button
            type="button"
            onClick={onNext}
            disabled={nextDisabled}
            className="question-btn primary"
          >
            {nextLabel || (isLastCard ? 'Complete' : 'Next')}
          </button>
        )}
      </div>
    </motion.div>
  );
};

export default QuestionCard;
